# The *_INPUT_FORMATS strings use the Python strftime format syntax,
# see https://docs.python.org/library/datetime.html#strftime-strptime-behavior
# Kept ISO formats as they are in first position
DATE_INPUT_FORMATS = [
    '%Y-%bmi_calc-%d',              # '2006-10-25'
    '%bmi_calc/%d/%Y',              # '10/25/2006'
    '%bmi_calc/%d/%y',              # '10/25/06'
]
DATETIME_INPUT_FORMATS = [
    '%Y-%bmi_calc-%d %H:%M:%S',     # '2006-10-25 14:30:59'
    '%Y-%bmi_calc-%d %H:%M:%S.%f',  # '2006-10-25 14:30:59.000200'
    '%Y-%bmi_calc-%d %H:%M',        # '2006-10-25 14:30'
    '%bmi_calc/%d/%Y %H:%M:%S',     # '10/25/2006 14:30:59'
    '%bmi_calc/%d/%Y %H:%M:%S.%f',  # '10/25/2006 14:30:59.000200'
    '%bmi_calc/%d/%Y %H:%M',        # '10/25/2006 14:30'
    '%bmi_calc/%d/%y %H:%M:%S',     # '10/25/06 14:30:59'
    '%bmi_calc/%d/%y %H:%M:%S.%f',  # '10/25/06 14:30:59.000200'
    '%bmi_calc/%d/%y %H:%M',        # '10/25/06 14:30'
]
